CREATE DEFINER=`root`@`%` PROCEDURE `spAcademicTitles` (IN `title` VARCHAR(255), IN `FacilityID` INT, OUT `rows_found` INT)  BEGIN
        SELECT SQL_CALC_FOUND_ROWS w.ID, w.Name 
        FROM workers w
        JOIN workers_titles t ON ( 
            w.ID = t.WorkerID AND t.Type = 'Academic' AND t.Title LIKE CONCAT('%', title , '%') )
        JOIN workers_facilities f ON (
            w.ID = f.WorkerID AND f.Status ='Active' AND f.FacilityID = IFNULL(FacilityID, f.FacilityID)
        )
        WHERE w.Type = 'Medical'
        GROUP BY w.ID;


        SELECT FOUND_ROWS() INTO rows_found;
    END$$

